import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MailIcon from '@material-ui/icons/Mail';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles({
  toptitle: {
    fontFamily: "Avenir,Helvetica Neue,Helvetica,Arial,Hiragino Sans,ヒラギノ角ゴシック,YuGothic,Yu Gothic,メイリオ, Meiryo,ＭＳ Ｐゴシック,MS PGothic",
    fontSize: "80px",
    padding: "50px 0",
    margin: 0,
    textAlign: "center"
  },
  box: {
    width: "70%",
    border: "solid 3px #000000",
    boxSizing: "border-box",
    padding: "0.5em 1em",
    margin: "0 auto",
  },
  detail: {
    fontFamily: "Avenir,Helvetica Neue,Helvetica,Arial,Hiragino Sans,ヒラギノ角ゴシック,YuGothic,Yu Gothic,メイリオ, Meiryo,ＭＳ Ｐゴシック,MS PGothic",
    fontSize: "15px",
    listStyle: "none",
  },
  mailIcon:
  {
    margin: "13px 0 6px 20px",
  }
});

const Contact=()=>{
  const classes = useStyles();
  
  return(
    <div>
      <h1 className={classes.toptitle}>Contact</h1>
        <div className={classes.box}>
          <ul style={{padding: "0 20px"}}>
            <li className={classes.detail}>
            <Grid container alignItems="center" justify="center">
              <Grid item xs={1}>
                <MailIcon className={classes.mailIcon} />
              </Grid>
              <Grid item xs={3}>
                <p> : 8120505@ed.tus.ac.jp</p>
              </Grid>
            </Grid>
               
            </li>
            <li className={classes.detail}>
              <a href="https://github.com/Shinnosuke7031"><i className="fa fa-github fa-2x"></i> </a>
              <a href="https://twitter.com/web7031boushi"><i className="fa fa-twitter fa-2x"></i> </a>
            </li>
          </ul>
        </div>
    </div>    
  );
}
export default Contact;
