import React, { useState } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import CssBaseline from '@material-ui/core/CssBaseline';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import { makeStyles } from '@material-ui/core/styles';
import { purple } from '@material-ui/core/colors';


const useStyles = makeStyles({
  list: {
    width: 250,
  },
  title: {
    flexGrow: 1,
    color: purple[800]
  },
  menuButton: {
    marginLeft: -15,
  },
  twitter: {
    marginLeft: 10,
  }
});


export default function Header() {

  const[menuClick, setMenuClick] = useState(false);
  const classes = useStyles(); //スタイルを決める
  const handleMenuClick = () => {
    if(menuClick) setMenuClick(false);
    else setMenuClick(true);
  } 


  let menuList;
  if(menuClick){
    menuList = (
      <p>menuList</p>
    )
  }


  return (
    <React.Fragment>
    <CssBaseline />
      <AppBar position="static" style={{ background: '#d1c4e9' }}>
        <Toolbar>
          {menuList}
          <IconButton className={classes.menuButton} color="inherit" aria-label="Menu">
              <MenuIcon style={{ color: purple[800]}} onClick={()=>{handleMenuClick()}}/>
          </IconButton>
          <Typography variant="h6" className={classes.title} align="center" color="inherit">Shin-Nosuke</Typography>
          <a href="https://github.com/Shinnosuke7031"><i class="fa fa-github fa-2x" aria-hidden="true"></i></a>
          <a href="https://twitter.com/web7031boushi" className={classes.twitter}><i class="fa fa-twitter fa-2x" aria-hidden="true"></i></a>
        </Toolbar>
      </AppBar>
  </React.Fragment>
  );
}
