import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import nosukeMemo1 from './img/nosukeMemo1.jpg';
import nosukeMemo2 from './img/nosukeMemo2.jpg';
import chatAPP1 from './img/chatAPP1.jpg';
import chatAPP2 from './img/chatAPP2.jpg';
import MediaQuery from "react-responsive";

const useStyles = makeStyles({
  bbody: {
    margin: "0 auto",
    height: "1000px",
    backgroundColor: "#d3d3d3",
  },
  contentsTitle: {
    margin: "0 auto",
    paddingTop: "50px",
    fontFamily: "Avenir,Helvetica Neue,Helvetica,Arial,Hiragino Sans,ヒラギノ角ゴシック,YuGothic,Yu Gothic,メイリオ, Meiryo,ＭＳ Ｐゴシック,MS PGothic",
    fontSize: "80px",
    padding: "50px 0 10px 0",
    textAlign: "center",
  },
  memoTitle: {
    fontFamily: "Avenir,Helvetica Neue,Helvetica,Arial,Hiragino Sans,ヒラギノ角ゴシック,YuGothic,Yu Gothic,メイリオ, Meiryo,ＭＳ Ｐゴシック,MS PGothic",
    fontSize: "30px",
    textAlign: "center",
    position: "absolute",
    backgroundColor: "#d3d3d3",
    top: "3px",
    left: "0",
    width: "175px",
    zIndex: 1
  },
  portBox: {
    margin: "20px auto",
    border: "solid 3px #000000",
    boxSizing: "border-box",
    width: "80%",
    padding: "40px 10px",
    position: "relative",
  },
  imgLink: {
    display: "flex",
    justifyContent: "space-evenly",
    margin: "0 auto",
  },
  imgLinkMob:
  {
    display: "flex",
    justifyContent: "space-evenly",
    textAlign: "center",
    margin: "0 auto",
    flexDirection: "column",
  },
  image: {
    width: "45%",
    height: "auto",
  },
  imageMob: {
    width: "90%",
    height: "auto",
    margin: "10px auto",
  },
  pTag:  {
    fontFamily: "Avenir,Helvetica Neue,Helvetica,Arial,Hiragino Sans,ヒラギノ角ゴシック,YuGothic,Yu Gothic,メイリオ, Meiryo,ＭＳ Ｐゴシック,MS PGothic",
    marginTop: "30px",
    textAlign: "center",
    fontSize: "20px",
  },
  pTagMob: {
    fontFamily: "Avenir,Helvetica Neue,Helvetica,Arial,Hiragino Sans,ヒラギノ角ゴシック,YuGothic,Yu Gothic,メイリオ, Meiryo,ＭＳ Ｐゴシック,MS PGothic",
    marginTop: "10px",
    textAlign: "center",
    fontSize: "15px",
  }
});


const Portfolio = () => {

  const classes=useStyles();
  const contents=[
    {
      name: 'memoApp', url: 'http://18.219.131.219:3000/', 
      img1: nosukeMemo2, img2: nosukeMemo1,
      text: '初めて作ったアプリケーション (HTML/CSS, Node.js, MySQL)'
    },
    {
      name: 'chatApp', url: 'http://test-nodejs.eba-fwryman3.ap-northeast-1.elasticbeanstalk.com/', 
      img1: chatAPP1, img2: chatAPP2,
      text: 'AWS-EB, RDSを用いて作成した掲示板APP (React.js, Node.js, MySQL)'
    }
  ]

  return (
    <div className={classes.bbody}>
      <h1 className={classes.contentsTitle}>Practice</h1>
      
      <MediaQuery query="(min-width: 769px)">
        <ul>
          {contents.map((content)=>(
            <div className={classes.portBox}>
              <a href={content.url} target="_blank" className={classes.imgLink}>
                <img className={classes.image} src={content.img1}/>
                <img className={classes.image} src={content.img2}/>
              </a>
              <p className={classes.pTag}>{ content.text }</p>
            </div>
          ))
          }
        </ul>
        
      </MediaQuery>
      <MediaQuery query="(max-width: 768px)">
        <ul>
          {contents.map((content)=>(
            <div className={classes.portBox}>
              <p className={classes.pTagMob}>{ content.text }</p>
              <a href={content.url} target="_blank" className={classes.imgLinkMob}>
                <img className={classes.imageMob} src={content.img1}/>
                <img className={classes.imageMob} src={content.img2}/>
              </a>
            </div>
          ))
          }
        </ul>
      </MediaQuery>
      
    </div>
  );
};

export default Portfolio;
