import React, { useState } from 'react';
import Top from './mainComponents/Top'
import Aboutme from './mainComponents/Aboutme'
import { makeStyles } from '@material-ui/core/styles';
import { deepPurple } from '@material-ui/core/colors';

const useStyles = makeStyles({
  mainDiv: {
    margin: "0 auto",
    width: "100%",
    height: "400px",
    backgroundColor: "#d3d3d3",
  },
});

const Main: React.FunctionComponent<Props> = ({props}) => {
  const classes=useStyles();
  
  let mainContent;
  if(props.mainType==="top"){
    mainContent=(
      <Top toggleDrawer={props.toggleDrawer}/>
    );
  }else if(props.mainType==="aboutMe"){
    mainContent=(
      <Aboutme/>
    );
  }


  return (
    <div className={classes.mainDiv}>
      {mainContent}
    </div>
  );
}

export default Main;
