import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
  toptitle: {
    fontFamily: "Avenir,Helvetica Neue,Helvetica,Arial,Hiragino Sans,ヒラギノ角ゴシック,YuGothic,Yu Gothic,メイリオ, Meiryo,ＭＳ Ｐゴシック,MS PGothic",
    fontSize: "80px",
    padding: "50px 0",
    margin: 0,
    textAlign: "center"
  },
  box: {
    width: "70%",
    border: "solid 3px #000000",
    boxSizing: "border-box",
    padding: "0.5em 1em",
    margin: "0 auto",
  },
  detail: {
    fontFamily: "Avenir,Helvetica Neue,Helvetica,Arial,Hiragino Sans,ヒラギノ角ゴシック,YuGothic,Yu Gothic,メイリオ, Meiryo,ＭＳ Ｐゴシック,MS PGothic",
    fontSize: "15px",
    listStyle: "none",
  },
  text:
  {
    fontFamily: "Avenir,Helvetica Neue,Helvetica,Arial,Hiragino Sans,ヒラギノ角ゴシック,YuGothic,Yu Gothic,メイリオ, Meiryo,ＭＳ Ｐゴシック,MS PGothic",
    fontSize: "15px",
    padding: "0 20px",
  }
});

const Contact=()=>{
  const classes = useStyles();
  
  return(
    <div>
      <h1 className={classes.toptitle}>Contact</h1>
        <div className={classes.box}>
          <ul style={{padding: "0 20px"}}>
            <li className={classes.detail}>
            <i class="fas fa-envelope" fa-2x></i><p> : 8120505@ed.tus.ac.jp</p>
            </li>
            <li className={classes.detail}>
               <MailIcon /><p> : 8120505@ed.tus.ac.jp</p>
            </li>
          </ul>
        </div>
    </div>    
  );
}
export default Contact;
