import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import nosukeMemo1 from './img/nosukeMemo1.jpg';
import nosukeMemo2 from './img/nosukeMemo2.jpg';

const useStyles = makeStyles({
  body: {
    margin: "0 auto",
  },
  image: {
    width: "35%",
    height: "auto",
  },
  imgLink: {
    display: "block",
  }
});


const Portfolio = () => {

  const classes=useStyles();
  const contents={
    name: 'memoApp', url: 'http://18.219.131.219:3000/', 
    img1: nosukeMemo1, img2: nosukeMemo2,
    text: '初めて作ったアプリケーション (HTML/CSS, Node.js, MySQL)'
  };
  console.log(nosukeMemo1);
  console.log(nosukeMemo2);


  return (
    <div>
      <div className={classes.body}>
        <h1>{contents.name}</h1>
        <a href={contents.url}>
          <img src={contents.img1}/>
          <img src={contents.img2}/>
        </a>
        <p>{ contents.text }</p>
      </div>
    </div>
  );
};

export default Portfolio;
