import React, { useState } from 'react';
import {useSpring, animated} from 'react-spring';
import { makeStyles } from '@material-ui/core/styles';
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles({
  pTag: {
    margin: "0 auto",
    textAlign: "center",
    padding: 0,
    width: "70%",
    padding: "50px 0"
  },
  topdivdiv: {
    fontFamily: "arial black",
    fontWeight: 600,
    fontSize: 100,
    willChange: "opacity",
    height: "100%"
  },
  icon: {
    fontSize: 60,
    display: "block"
  },
  menutext: {
    fontSize: 50,
    textAlign: "center"
  }
});

const Top: React.FunctionComponent<{toggleDrawer: any}> = ({toggleDrawer}) => {
  const classes = useStyles();
  const [state, toggle] = useState(true)
  const { x } = useSpring({ from: { x: 0 }, x: state ? 1 : 0, config: { duration: 1000 } })
  return (
    <div className={classes.topdivdiv}>
      <animated.div
        style={{
          opacity: x.interpolate({ range: [0, 1], output: [0.3, 1] }),
          transform: x
            .interpolate({
              range: [0, 0.25, 0.35, 0.45, 0.55, 0.65, 0.75, 1],
              output: [1, 0.97, 0.9, 1.1, 0.9, 1.1, 1.03, 1]
            })
            .interpolate(x => `scale(${x})`)
        }}>

        <p className={classes.pTag} onClick={() => toggle(!state)}>Welcome</p>

        <Grid container alignItems="center" justify="center" onClick={() => toggleDrawer(true)}>
          <Grid item xs={3}>
            <p className={classes.menutext}>Click</p>
          </Grid>
          <Grid item xs={1}>
            <ArrowBackIcon className={classes.icon}/>
          </Grid>
        </Grid>
      </animated.div>
    </div>
  );
}

export default Top;
